/* Demo Note:  This demo uses a FileProgress class that handles the UI for displaying the file name and percent complete.
The FileProgress class is not part of SWFUpload.
 */

/* **********************
 Event Handlers
 These are my custom event handlers to make my
 web application behave the way I went when SWFUpload
 completes different tasks.  These aren't part of the SWFUpload
 package.  They are part of my application.  Without these none
 of the actions SWFUpload makes will show up in my application.
 ********************** */
function fileQueued(file){try{var progress=new FileProgress(file,this.customSettings.progressTarget);progress.setStatus("Pending...");progress.toggleCancel(true,this)}catch(ex){this.debug(ex)}}function fileQueueError(file,errorCode,message){try{if(errorCode===SWFUpload.QUEUE_ERROR.QUEUE_LIMIT_EXCEEDED){alert("You have attempted to queue too many files.\n"+(message===0?"You have reached the upload limit.":"You may select "+(message>1?"up to "+message+" files.":"one file.")));return}var progress=new FileProgress(file,this.customSettings.progressTarget);progress.setError();progress.toggleCancel(false);switch(errorCode){case SWFUpload.QUEUE_ERROR.FILE_EXCEEDS_SIZE_LIMIT:progress.setStatus("File is too big.");this.debug("Error Code: File too big, File name: "+file.name+", File size: "+file.size+", Message: "+message);break;case SWFUpload.QUEUE_ERROR.ZERO_BYTE_FILE:progress.setStatus("Cannot upload Zero Byte files.");this.debug("Error Code: Zero byte file, File name: "+file.name+", File size: "+file.size+", Message: "+message);break;case SWFUpload.QUEUE_ERROR.INVALID_FILETYPE:progress.setStatus("Invalid File Type.");this.debug("Error Code: Invalid File Type, File name: "+file.name+", File size: "+file.size+", Message: "+message);break;default:if(file!==null){progress.setStatus("Unhandled Error")}this.debug("Error Code: "+errorCode+", File name: "+file.name+", File size: "+file.size+", Message: "+message);break}}catch(ex){this.debug(ex)}}function fileDialogComplete(numFilesSelected,numFilesQueued){try{if(numFilesSelected>0){document.getElementById(this.customSettings.cancelButtonId).disabled=false}this.startUpload()}catch(ex){this.debug(ex)}}function uploadStart(file){try{var progress=new FileProgress(file,this.customSettings.progressTarget);progress.setStatus("准备上传...");progress.toggleCancel(true,this)}catch(ex){}return true}function uploadProgress(file,bytesLoaded,bytesTotal){try{var percent=Math.ceil((bytesLoaded/bytesTotal)*100);var progress=new FileProgress(file,this.customSettings.progressTarget);progress.setProgress(percent);progress.setStatus("文件上传中..."+"&nbsp;"+percent+"%")}catch(ex){this.debug(ex)}}function uploadSuccess(file,serverData){try{var progress=new FileProgress(file,this.customSettings.progressTarget);progress.setComplete();createElementsToForm(file,serverData);progress.setStatus("文件上传成功.");progress.toggleCancel(false)}catch(ex){this.debug(ex)}}function uploadError(file,errorCode,message){try{var progress=new FileProgress(file,this.customSettings.progressTarget);progress.setError();progress.toggleCancel(false);switch(errorCode){case SWFUpload.UPLOAD_ERROR.HTTP_ERROR:progress.setStatus("Upload Error: "+message);this.debug("Error Code: HTTP Error, File name: "+file.name+", Message: "+message);break;case SWFUpload.UPLOAD_ERROR.UPLOAD_FAILED:progress.setStatus("Upload Failed.");this.debug("Error Code: Upload Failed, File name: "+file.name+", File size: "+file.size+", Message: "+message);break;case SWFUpload.UPLOAD_ERROR.IO_ERROR:progress.setStatus("Server (IO) Error");this.debug("Error Code: IO Error, File name: "+file.name+", Message: "+message);break;case SWFUpload.UPLOAD_ERROR.SECURITY_ERROR:progress.setStatus("Security Error");this.debug("Error Code: Security Error, File name: "+file.name+", Message: "+message);break;case SWFUpload.UPLOAD_ERROR.UPLOAD_LIMIT_EXCEEDED:progress.setStatus("Upload limit exceeded.");this.debug("Error Code: Upload Limit Exceeded, File name: "+file.name+", File size: "+file.size+", Message: "+message);break;case SWFUpload.UPLOAD_ERROR.FILE_VALIDATION_FAILED:progress.setStatus("Failed Validation.  Upload skipped.");this.debug("Error Code: File Validation Failed, File name: "+file.name+", File size: "+file.size+", Message: "+message);break;case SWFUpload.UPLOAD_ERROR.FILE_CANCELLED:if(this.getStats().files_queued===0){document.getElementById(this.customSettings.cancelButtonId).disabled=true}progress.setStatus("Cancelled");progress.setCancelled();break;case SWFUpload.UPLOAD_ERROR.UPLOAD_STOPPED:progress.setStatus("Stopped");break;default:progress.setStatus("Unhandled Error: "+errorCode);this.debug("Error Code: "+errorCode+", File name: "+file.name+", File size: "+file.size+", Message: "+message);break}}catch(ex){this.debug(ex)}}function uploadComplete(file){if(this.getStats().files_queued===0){document.getElementById(this.customSettings.cancelButtonId).disabled=true}}function queueComplete(numFilesUploaded){var status=document.getElementById("doc_path");if(status){status.value="File is Uploaded"}}function createElementsToForm(file,serverData){if(!serverData){return null}var entity_id=SWFUpload.PATH.ENTITY_ID;var ctx=SWFUpload.PATH.CTX;var upload_single_file=SWFUpload.PATH.UPLOAD_SINGLE_FILE;var divCreateElementsToForm=$("#divCreateElementsToForm");if(divCreateElementsToForm){var real_url=ctx+"/"+serverData;var rel_url=serverData;var fileId="fileName_"+new Date().getTime();var fileName='文件名称：<input id="'+fileId+'" value="'+file.name+'" type="text" class="fileNameClass" name="file_name" maxlength="100"></input>';var delElement='<span class="delElement" onclick="deleteFile(\''+entity_id+'\', \''+fileId+'\', \''+encodeURI(rel_url)+'\')" title="删除附件">&nbsp;删除</span>';var fileHiddenPath='<input value="'+rel_url+'" type="hidden" name="file_path"></input>';var divWrapper="<div>"+fileName+delElement+fileHiddenPath+"</div>";if(upload_single_file){divCreateElementsToForm.empty()}divCreateElementsToForm.append(divWrapper)}}function deleteFile(entity_id,fileid,file_path){if(confirm("确定删除附件！")){var delete_url=SWFUpload.PATH.DELETE_URL;var delete_method=SWFUpload.PATH.DELETE_METHOD;$("#"+fileid).parent().remove();$.post(delete_url,{method:delete_method,file_path:file_path,id:entity_id},function(result){})}}
